import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {

  currentRoute: string = '/';

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.router.events.subscribe(event => {
      if (event instanceof NavigationEnd) {
        this.currentRoute = event.url;
      }
    });
  }
}
<p-menubar class="header-bar dark-header">



  <ng-template pTemplate="end">
    <div class="nav-links">
      <a routerLink="/" routerLinkActive="active-link" [routerLinkActiveOptions]="{ exact: true }" class="nav-link">Home</a>
      <a routerLink="/add-cart" routerLinkActive="active-link" class="nav-link">Cart</a>
      <a routerLink="/contact" routerLinkActive="active-link" class="nav-link">Contact</a>
    </div>
  </ng-template>

</p-menubar>
