// header.component.ts
import { Component, Input, OnInit } from '@angular/core';
import { MenuItem } from 'primeng/api';
import { PruductService } from '../service/pruduct.service';
import { IProduct, IMenuItem } from '../interface/product';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
items: any[] = [];
  searchValue: string = "";
  cartCountChange: number = 0;

  constructor(private productService: ProductService) {}

  ngOnInit() {
    this.items = [
      { label: 'Home', routerLink: '/' },
      { label: 'Cart', routerLink: '/add-cart' },
      { label: 'Contact', routerLink: '/contact' }
    ];

    this.loadCartCount();
  }

  loadCartCount() {
    let data = localStorage.getItem("cart");
    if (data) {
      this.cartCountChange = JSON.parse(data).length;
    }
  }

  filterProducts() {
    this.productService.filterProducts(this.searchValue);
  }

}
